package main

import (
	"github.com/gorilla/mux"
	"net/http"
)

// BalanceInterface is interface for /balance root endpoint
type BalanceInterface interface { // Get is the handler for GET /balance
	// Send balance information request
	Get(http.ResponseWriter, *http.Request)
}

// BalanceInterfaceRoutes is routing for /balance root endpoint
func BalanceInterfaceRoutes(r *mux.Router, i BalanceInterface) {
	r.HandleFunc("/balance", i.Get).Methods("GET")
}
