package main

import (
	"github.com/gorilla/mux"
	"net/http"
)

// GettxsInterface is interface for /gettxs root endpoint
type GettxsInterface interface { // Get is the handler for GET /gettxs
	// Get a list of transactions (deposits and payments) along with their details
	// for the authenticated user's custody account.
	Get(http.ResponseWriter, *http.Request)
}

// GettxsInterfaceRoutes is routing for /gettxs root endpoint
func GettxsInterfaceRoutes(r *mux.Router, i GettxsInterface) {
	r.HandleFunc("/gettxs", i.Get).Methods("GET")
}
