package main

import (
	"github.com/gorilla/mux"
	"net/http"
)

// PayinvoiceInterface is interface for /payinvoice root endpoint
type PayinvoiceInterface interface { // Post is the handler for POST /payinvoice
	// Request a BOLT-11 compatible Lightning invoice to be paid by the custodian.
	// If the payment is successful, the respective amount + a fee is substracted
	// from the authenticated user's balance.
	Post(http.ResponseWriter, *http.Request)
}

// PayinvoiceInterfaceRoutes is routing for /payinvoice root endpoint
func PayinvoiceInterfaceRoutes(r *mux.Router, i PayinvoiceInterface) {
	r.HandleFunc("/payinvoice", i.Post).Methods("POST")
}
