package main

import (
	"github.com/gorilla/mux"

	"github.com/yojoe/lightninghub-api/go/server/handlers/addinvoice"
	"github.com/yojoe/lightninghub-api/go/server/handlers/auth"
	"github.com/yojoe/lightninghub-api/go/server/handlers/balance"
	"github.com/yojoe/lightninghub-api/go/server/handlers/create"
	"github.com/yojoe/lightninghub-api/go/server/handlers/decodeinvoice"
	"github.com/yojoe/lightninghub-api/go/server/handlers/getbtc"
	"github.com/yojoe/lightninghub-api/go/server/handlers/getinfo"
	"github.com/yojoe/lightninghub-api/go/server/handlers/getpending"
	"github.com/yojoe/lightninghub-api/go/server/handlers/gettxs"
	"github.com/yojoe/lightninghub-api/go/server/handlers/getuserinvoices"
	"github.com/yojoe/lightninghub-api/go/server/handlers/payinvoice"
)

func initRoutes(r *mux.Router) {

	AddinvoiceInterfaceRoutes(r, addinvoice.AddinvoiceAPI{})
	AuthInterfaceRoutes(r, auth.AuthAPI{})
	BalanceInterfaceRoutes(r, balance.BalanceAPI{})
	CreateInterfaceRoutes(r, create.CreateAPI{})
	DecodeinvoiceInterfaceRoutes(r, decodeinvoice.DecodeinvoiceAPI{})
	GetbtcInterfaceRoutes(r, getbtc.GetbtcAPI{})
	GetinfoInterfaceRoutes(r, getinfo.GetinfoAPI{})
	GetpendingInterfaceRoutes(r, getpending.GetpendingAPI{})
	GettxsInterfaceRoutes(r, gettxs.GettxsAPI{})
	GetuserinvoicesInterfaceRoutes(r, getuserinvoices.GetuserinvoicesAPI{})
	PayinvoiceInterfaceRoutes(r, payinvoice.PayinvoiceAPI{})
}
