package types

import (
	base_types_types "github.com/yojoe/lightninghub-api/go/server/types/base_types/types"
	"gopkg.in/validator.v2"
)

type BTCTransaction struct {
	Account         string                      `json:"account" validate:"nonzero"`
	Address         base_types_types.BTCAddress `json:"address" validate:"nonzero"`
	Amount          float64                     `json:"amount" validate:"nonzero"`
	Blockhash       base_types_types.HexString  `json:"blockhash" validate:"nonzero"`
	Blockindex      int                         `json:"blockindex" validate:"min=0,nonzero"`
	Blocktime       int                         `json:"blocktime" validate:"min=0,nonzero"`
	Category        string                      `json:"category" validate:"nonzero"`
	Confirmations   int                         `json:"confirmations" validate:"min=0,nonzero"`
	Time            int                         `json:"time" validate:"min=0,nonzero"`
	Timereceived    int                         `json:"timereceived" validate:"min=0,nonzero"`
	Txid            base_types_types.HexString  `json:"txid" validate:"nonzero"`
	Walletconflicts any                         `json:"walletconflicts" validate:"nonzero"`
}

func (s BTCTransaction) Validate() error {

	return validator.Validate(s)
}
