// Details of an invoice
type Invoice struct {
	Add_index       int                        `json:"add_index" validate:"nonzero"`
	Amt             int                        `json:"amt" validate:"nonzero"`
	Description     string                     `json:"description" validate:"nonzero"`
	Expire_time     int                        `json:"expire_time" validate:"nonzero"`
	Ispaid          bool                       `json:"ispaid"`
	Pay_req         bolt_types_types.BOLT_11   `json:"pay_req" validate:"nonzero"`
	Payment_hash    base_types_types.HexString `json:"payment_hash" validate:"nonzero"`
	Payment_request bolt_types_types.BOLT_11   `json:"payment_request" validate:"nonzero"`
	R_hash          base_types_types.Buffer    `json:"r_hash" validate:"nonzero"`
	Timestamp       int                        `json:"timestamp" validate:"nonzero"`
	Type            EnumInvoiceType            `json:"type" validate:"nonzero"`
}

func (s Invoice) Validate() error {

	return validator.Validate(s)
}
