package types

import (
	"gopkg.in/validator.v2"
)

// Details of a payment
type Payment struct {
	Fee       int             `json:"fee" validate:"min=0,nonzero"`
	Memo      string          `json:"memo,omitempty"`
	Timestamp int             `json:"timestamp" validate:"nonzero"`
	Type      EnumPaymentType `json:"type" validate:"nonzero"`
	Value     int             `json:"value" validate:"min=0,nonzero"`
}

func (s Payment) Validate() error {

	return validator.Validate(s)
}
